mapper.update:
UPDATE asset_detail
SET asset_category_code          = #{assetCategoryCode},
    amount                       = #{amount},
    registered_at                = #{registeredAt},
    end_date                     = #{endDate},
    asset_name                   = #{assetName},
    business_type                = #{businessType}
WHERE asset_id = #{assetId}
  AND email = #{email}

mapper.delete:
DELETE
FROM asset_detail
WHERE asset_id = #{assetId}
  AND email = #{userEmail}
