mapper.select:
SELECT asset_category_code, amount
FROM asset_status
WHERE email = #{email}

mapper.update:
UPDATE asset_status
SET asset_category_code = #{assetCategoryCode},
    amount              = #{amount},
    asset_name          = #{assetName},
    business_type       = #{businessType}
WHERE asset_id = #{assetId}
  AND email = #{email}

mapper.delete:
DELETE
FROM asset_status
WHERE asset_id = #{assetId}
  AND email = #{userEmail}
